// SPDX-License-Identifier: MIT
pragma solidity ^0.8.21;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";

contract BVIXToken is ERC20, AccessControl {
    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");

    constructor(address initialOwner) ERC20("BVIX Token", "BVIX") {
        _grantRole(DEFAULT_ADMIN_ROLE, initialOwner);
        _grantRole(MINTER_ROLE, initialOwner);
    }

    function mint(address to, uint256 amount) external onlyRole(MINTER_ROLE) {
        _mint(to, amount);
    }

    function burn(address from, uint256 amount) external onlyRole(MINTER_ROLE) {
        _burn(from, amount);
    }
}

contracts/ChainlinkOracle.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.21;

import "@chainlink/contracts/src/v0.8/interfaces/AggregatorV3Interface.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";

/**
 * @title ChainlinkOracle - Chainlink Integration for Volatility Indices
 * @notice Wrapper around Chainlink price feeds with fallback mechanisms
 * @dev BVIX pegged to Volmex BVIV Index, EVIX pegged to Volmex EVIV index
 */
contract ChainlinkOracle is AccessControl, Pausable {
    bytes32 public constant GOVERNOR_ROLE = keccak256("GOVERNOR_ROLE");
    bytes32 public constant PAUSER_ROLE = keccak256("PAUSER_ROLE");

    AggregatorV3Interface public priceFeed;
    uint256 public fallbackPrice;
    uint256 public stalenessThreshold = 1 hours;
    bool public useFallback = false;

    event FallbackPriceUpdated(uint256 oldPrice, uint256 newPrice);
    event StalenessThresholdUpdated(uint256 oldThreshold, uint256 newThreshold);
    event FallbackModeToggled(bool useFallback);

    error StalePrice();
    error InvalidPrice();
    error PriceFeedNotAvailable();

    constructor(
        address _priceFeed,
        uint256 _fallbackPrice,
        address _governor
    ) {
        if (_priceFeed != address(0)) {
            priceFeed = AggregatorV3Interface(_priceFeed);
        }

        fallbackPrice = _fallbackPrice;

        // Setup roles
        _grantRole(DEFAULT_ADMIN_ROLE, _governor);
        _grantRole(GOVERNOR_ROLE, _governor);
        _grantRole(PAUSER_ROLE, _governor);

        // Revoke admin role from deployer
        _revokeRole(DEFAULT_ADMIN_ROLE, msg.sender);
    }

    /**
     * @notice Get current price from Chainlink or fallback
     * @return Current price in 6 decimals
     */
    function getPrice() external view returns (uint256) {
        if (useFallback || address(priceFeed) == address(0)) {
            return fallbackPrice;
        }

        try priceFeed.latestRoundData() returns (
            uint80 roundId,
            int256 price,
            uint256 startedAt,
            uint256 updatedAt,
            uint80 answeredInRound
        ) {
            // Check for stale price
            if (block.timestamp - updatedAt > stalenessThreshold) {
                revert StalePrice();
            }

            // Check for invalid price
            if (price <= 0) {
                revert InvalidPrice();
            }

            // Convert Chainlink price (8 decimals) to 6 decimals
            return uint256(price) / 100;

        } catch {
            revert PriceFeedNotAvailable();
        }
    }

    /**
     * @notice Get price with staleness check
     * @return Current price and whether it's stale
     */
    function getPriceWithStaleness() external view returns (uint256, bool) {
        if (useFallback || address(priceFeed) == address(0)) {
            return (fallbackPrice, false);
        }

        try priceFeed.latestRoundData() returns (
            uint80 roundId,
            int256 price,
            uint256 startedAt,
            uint256 updatedAt,
            uint80 answeredInRound
        ) {
            bool isStale = block.timestamp - updatedAt > stalenessThreshold;
            return (uint256(price) / 100, isStale);

        } catch {
            return (fallbackPrice, true);
        }
    }

    /**
     * @notice Update fallback price
     * @param _newPrice New fallback price in 6 decimals
     */
    function setFallbackPrice(uint256 _newPrice) external onlyRole(GOVERNOR_ROLE) {
        uint256 oldPrice = fallbackPrice;
        fallbackPrice = _newPrice;
        emit FallbackPriceUpdated(oldPrice, _newPrice);
    }

    /**
     * @notice Update staleness threshold
     * @param _newThreshold New threshold in seconds
     */
    function setStalenessThreshold(uint256 _newThreshold) external onlyRole(GOVERNOR_ROLE) {
        uint256 oldThreshold = stalenessThreshold;
        stalenessThreshold = _newThreshold;
        emit StalenessThresholdUpdated(oldThreshold, _newThreshold);
    }

    /**
     * @notice Toggle fallback mode
     * @param _useFallback Whether to use fallback price
     */
    function setFallbackMode(bool _useFallback) external onlyRole(GOVERNOR_ROLE) {
        useFallback = _useFallback;
        emit FallbackModeToggled(_useFallback);
    }

    /**
     * @notice Update price feed address
     * @param _newPriceFeed New Chainlink price feed address
     */
    function setPriceFeed(address _newPriceFeed) external onlyRole(GOVERNOR_ROLE) {
        priceFeed = AggregatorV3Interface(_newPriceFeed);
    }

    /**
     * @notice Pause oracle operations
     */
    function pause() external onlyRole(PAUSER_ROLE) {
        _pause();
    }

    /**
     * @notice Unpause oracle operations
     */
    function unpause() external onlyRole(PAUSER_ROLE) {
        _unpause();
    }

    /**
     * @notice Get Chainlink price feed metadata
     * @return decimals Number of decimals in the price feed
     * @return description Description of the price feed
     * @return version Version of the price feed
     */
    function getPriceFeedMetadata() external view returns (uint8, string memory, uint256) {
        if (address(priceFeed) == address(0)) {
            return (0, "No price feed", 0);
        }

        try priceFeed.decimals() returns (uint8 decimals) {
            try priceFeed.description() returns (string memory description) {
                try priceFeed.version() returns (uint256 version) {
                    return (decimals, description, version);
                } catch {
                    return (decimals, description, 0);
                }
            } catch {
                return (decimals, "Unknown", 0);
            }
        } catch {
            return (0, "Invalid price feed", 0);
        }
    }
}

contracts/EVIXMintRedeem.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.21;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "./EVIXToken.sol";

// Oracle interface for price feeds
interface IOracle {
    function getPrice() external view returns (int256);
}

contract EVIXMintRedeem is Ownable {
    IERC20 public immutable usdc;
    EVIXToken public immutable evix;
    IOracle public oracle;

    // Fee in basis points (30 = 0.3%)
    uint256 public constant REDEEM_FEE_BPS = 30;
    uint256 public constant BASIS_POINTS = 10000;

    event Mint(address indexed user, uint256 usdcAmount, uint256 evixAmount);
    event Redeem(address indexed user, uint256 evixAmount, uint256 usdcAmount);

    constructor(
        address _usdc,
        address _evix,
        address _oracle,
        address initialOwner
    ) Ownable(initialOwner) {
        usdc = IERC20(_usdc);
        evix = EVIXToken(_evix);
        oracle = IOracle(_oracle);
    }

    function mint(uint256 usdcAmount) external {
        require(usdcAmount > 0, "amount = 0");

        // Get current EVIX price from oracle
        int256 price = oracle.getPrice();
        require(price > 0, "invalid price");

        // Calculate EVIX amount: usdcAmount / price
        // USDC has 6 decimals, EVIX has 18 decimals, price has 18 decimals
        uint256 evixAmount = (usdcAmount * 1e30) / uint256(price);

        // Transfer USDC from user to this contract
        require(usdc.transferFrom(msg.sender, address(this), usdcAmount), "USDC transfer failed");

        // Mint EVIX tokens to user
        evix.mint(msg.sender, evixAmount);

        emit Mint(msg.sender, usdcAmount, evixAmount);
    }

    function redeem(uint256 evixAmount) external {
        require(evixAmount > 0, "amount = 0");

        // Get current EVIX price from oracle
        int256 price = oracle.getPrice();
        require(price > 0, "invalid price");

        // Calculate USDC amount: evixAmount * price
        uint256 usdcAmount = (evixAmount * uint256(price)) / 1e30;

        // Apply redemption fee
        uint256 fee = (usdcAmount * REDEEM_FEE_BPS) / BASIS_POINTS;
        uint256 usdcAfterFee = usdcAmount - fee;

        // Burn EVIX tokens from user
        evix.burn(msg.sender, evixAmount);

        // Transfer USDC to user (after fee)
        require(usdc.transfer(msg.sender, usdcAfterFee), "USDC transfer failed");

        emit Redeem(msg.sender, evixAmount, usdcAfterFee);
    }

    function setOracle(address _oracle) external onlyOwner {
        oracle = IOracle(_oracle);
    }

    function withdrawFees() external onlyOwner {
        uint256 balance = usdc.balanceOf(address(this));
        require(usdc.transfer(owner(), balance), "USDC transfer failed");
    }
}

contracts/EVIXMintRedeemSimple.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.21;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "./EVIXToken.sol";
import "./PriceOracle.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title EVIX MintRedeem Simple - Basic minting without complex logic
 */
contract EVIXMintRedeemSimple is Ownable {
    IERC20 public usdc;
    EVIXToken public evix;
    PriceOracle public oracle;

    uint256 public mintFee = 30;  // 0.30%

    event Mint(address indexed user, uint256 usdcAmount, uint256 evixMinted);
    event Redeem(address indexed user, uint256 evixAmount, uint256 usdcRedeemed);

    constructor(
        address _usdc,
        address _evix,
        address _oracle,
        address initialOwner
    ) Ownable(initialOwner) {
        usdc = IERC20(_usdc);
        evix = EVIXToken(_evix);
        oracle = PriceOracle(_oracle);
    }

    /**
     * @notice Simple mint function
     */
    function mint(uint256 usdcAmount) external returns (uint256) {
        require(usdcAmount > 0, "Amount must be > 0");

        // Get price from oracle
        uint256 price = uint256(oracle.getPrice());
        require(price > 0, "Invalid price");

        // Calculate fee
        uint256 fee = (usdcAmount * mintFee) / 10000;
        uint256 netAmount = usdcAmount - fee;

        // Calculate EVIX to mint: netAmount / price
        // USDC has 6 decimals, EVIX has 18 decimals, price has 6 decimals
        uint256 evixToMint = (netAmount * 1e18) / price;

        require(evixToMint > 0, "Amount too small");

        // Transfer USDC from user
        require(usdc.transferFrom(msg.sender, address(this), usdcAmount), "Transfer failed");

        // Mint EVIX to user
        evix.mint(msg.sender, evixToMint);

        emit Mint(msg.sender, usdcAmount, evixToMint);
        return evixToMint;
    }

    /**
     * @notice Redeem EVIX for USDC
     */
    function redeem(uint256 evixAmount) external returns (uint256) {
        require(evixAmount > 0, "Amount must be > 0");
        require(evix.balanceOf(msg.sender) >= evixAmount, "Insufficient balance");

        // Get price from oracle
        uint256 price = uint256(oracle.getPrice());
        require(price > 0, "Invalid price");

        // Calculate USDC value: evixAmount * price
        uint256 usdcValue = (evixAmount * price) / 1e18;

        // Apply fee
        uint256 fee = (usdcValue * mintFee) / 10000;
        uint256 netUSDC = usdcValue - fee;

        require(usdc.balanceOf(address(this)) >= netUSDC, "Insufficient vault USDC");

        // Burn EVIX from user
        evix.burn(msg.sender, evixAmount);

        // Transfer USDC to user
        require(usdc.transfer(msg.sender, netUSDC), "Transfer failed");

        emit Redeem(msg.sender, evixAmount, netUSDC);
        return netUSDC;
    }

    /**
     * @notice Withdraw fees (owner only)
     */
    function withdrawFees() external onlyOwner {
        uint256 balance = usdc.balanceOf(address(this));
        require(usdc.transfer(owner(), balance), "Transfer failed");
    }
}

